"use strict";

// 依赖
class Server {
	render404(e){
		let me = this;
		let handle = me.handle;
		let response = handle.response;
		response.writeHead(404, {
			'Content-Type': 'text/html',
			'charset': 'utf-8'
		});
        response.end(JSON.stringify(e || 'something not found!'));
	}
	render304(){
		let me = this;
		let handle = me.handle;
		let response = handle.response;
		response.writeHead(304);
		response.end();
	}
};

module.exports = Server;








